const targetTypebot = $('Campanha').first().json.typebot;
const registros = items; // todos os registros recebidos no n√≥

const existe = registros.some(item => item.json.typebot === targetTypebot);

return [
  {
    json: {
      resultado: existe ? 'exist' : 'no',
      typebot: targetTypebot
    }
  }
];

const items = $input.all();
const contatosMap = {};

for (const item of items) {
  const data = item.json;

  // 1) Mapeia contatos do primeiro payload
  if (Array.isArray(data.payload)) {
    for (const user of data.payload) {
      // usa identifier ou phone_number como chave √∫nica
      const key = user.identifier || user.phone_number;
      if (!contatosMap[key]) {
        contatosMap[key] = {
          id_contato:  user.id,
          id_conversa: null,
          nome:        user.name,
          email:       user.email,
          identifier:  user.identifier,
          phone_number: user.phone_number,
        };
      }
    }
  }

  // 2) Mapeia conversas do segundo payload
  if (data.data?.payload && Array.isArray(data.data.payload)) {
    for (const conv of data.data.payload) {
      const sender = conv.meta?.sender;
      if (!sender) continue;
      const key = sender.identifier || sender.phone_number;
      if (!contatosMap[key]) {
        contatosMap[key] = {
          id_contato:  sender.id,
          id_conversa: conv.id,
          nome:        sender.name,
          email:       sender.email,
          identifier:  sender.identifier,
          phone_number: sender.phone_number,
        };
      } else if (!contatosMap[key].id_conversa) {
        contatosMap[key].id_conversa = conv.id;
      }
    }
  }
}

return Object.values(contatosMap);

